import React from "react";
import NoteContext from "./noteContext";

const NoteState=(props)=>{
    const s1={
        "name":"usama",
        "class":"10"
    }
    const [state, setstate] = useState(s1)
    update=()=>{
        setTimeout(() => {
            setstate({
                "name":"uj",
                "class":"14" 
            })
        }, 1000);    
    }
return(
<NoteContext.Provider value={state}>
    {props.children}
</NoteContext.Provider>
)}
export default NoteState;
